import sinon from 'sinon';
import {assert} from '@webex/test-helper-chai';

import MemberUtil from '@webex/plugin-meetings/src/member/util';
import Member from '@webex/plugin-meetings/src/member';


describe('member', () => {
  const sandbox = sinon.createSandbox();

  afterEach(() => {
    sandbox.restore();
  });

  it('test', async () => {
    sandbox.spy(MemberUtil, 'isHandRaised');

    const participant = {controls: {}};

    // eslint-disable-next-line no-unused-vars
    const member = new Member(participant);

    assert.calledOnce(MemberUtil.isHandRaised);
  });
});
