import {assert} from '@webex/test-helper-chai';

import MemberUtil from '@webex/plugin-meetings/src/member/util';


describe('isHandRaised', () => {
  it('throws error when there is no participant', () => {
    assert.throws(() => {
      MemberUtil.isHandRaised();
    }, 'Raise hand could not be processed, participant is undefined.');
  });

  it('return false when hand is not there in controls', () => {
    const participant = {
      controls: {}
    };

    assert.deepEqual(MemberUtil.isHandRaised(participant), false);
  });

  it('return true when hand raised is true', () => {
    const participant = {
      controls: {
        hand: {
          raised: true
        },
      }
    };

    assert.deepEqual(MemberUtil.isHandRaised(participant), true);
  });

  it('return false when hand raised is false', () => {
    const participant = {
      controls: {
        hand: {
          raised: false
        },
      }
    };

    assert.deepEqual(MemberUtil.isHandRaised(participant), false);
  });
});
